// 输入验证
const company = $input.item.json.body?.company;
const market = $input.item.json.body?.market || null;

if (!company || typeof company !== 'string' || company.trim() === '') {
  throw new Error('缺少必填参数: company (公司名称)');
}

if (company.length > 100) {
  throw new Error('公司名称过长，请限制在100字符以内');
}

const cleanCompany = company.trim();

return {
  json: {
    userInputCompany: cleanCompany,  // 保存用户原始输入
    company: cleanCompany,
    market: market,
    requestTime: new Date().toISOString()
  }
};

// 处理 AKShare API 返回的数据，生成财务报告
const response = $input.item.json;
const inputData = $('验证输入').item.json;

// 保持用户原始输入
const userInputCompany = inputData.userInputCompany;

if (!response.success || !response.data.stock_found) {
  return {
    json: {
      userInputCompany: userInputCompany,
      company: userInputCompany,
      stockCode: null,
      stockName: null,
      market: null,
      stockFound: false,
      hasFinancialData: false,
      financialReport: `## 注意\n\n未找到「${userInputCompany}」的股票信息。\n\n可能原因：\n1. 公司名称输入不完整或有误\n2. 该公司未在A股或港股上市\n\n请尝试输入准确的股票简称（如「贵州茅台」「腾讯控股」）。`,
      requestTime: inputData.requestTime
    }
  };
}

const stockInfo = response.data.stock_info;
const financial = response.data.financial;

// 格式化数字
function formatNum(val) {
  if (val === null || val === undefined || val === '' || val === '-') return '-';
  const num = parseFloat(val);
  if (isNaN(num)) return String(val);
  if (Math.abs(num) >= 1e8) return (num / 1e8).toFixed(2) + '亿';
  if (Math.abs(num) >= 1e4) return (num / 1e4).toFixed(2) + '万';
  return num.toFixed(2);
}

function formatPct(val) {
  if (val === null || val === undefined || val === '' || val === '-') return '-';
  const num = parseFloat(val);
  if (isNaN(num)) return String(val);
  return num.toFixed(2) + '%';
}

function safeGet(obj, key, defaultVal = '-') {
  if (!obj) return defaultVal;
  const val = obj[key];
  if (val === null || val === undefined || val === '') return defaultVal;
  return val;
}

// 生成报告
const marketLabel = stockInfo.market === 'A' ? (stockInfo.symbol.startsWith('SH') ? 'SH' : 'SZ') : 'HK';
let report = `# ${stockInfo.name}（${stockInfo.code}.${marketLabel}）财务分析报告\n\n`;
report += `**用户查询公司**：${userInputCompany}\n`;
report += `数据来源：AKShare (东方财富)  |  生成时间：${new Date().toLocaleString('zh-CN')}\n\n`;

// 资产负债表
const balance = financial.balance_sheet || [];
if (balance.length > 0) {
  report += `## 一、资产负债表（最近${Math.min(balance.length, 8)}期）\n\n`;
  report += `| 报告期 | 总资产 | 总负债 | 净资产 | 资产负债率 |\n`;
  report += `|--------|--------|--------|--------|-----------|\n`;
  for (const row of balance.slice(0, 8)) {
    const period = safeGet(row, 'REPORT_DATE', safeGet(row, '报告期', '-'));
    const periodStr = period !== '-' ? String(period).substring(0, 10) : '-';
    const totalAssets = formatNum(safeGet(row, 'TOTAL_ASSETS', safeGet(row, '资产总计')));
    const totalLiab = formatNum(safeGet(row, 'TOTAL_LIABILITIES', safeGet(row, '负债合计')));
    const totalEquity = formatNum(safeGet(row, 'TOTAL_EQUITY', safeGet(row, '股东权益合计')));
    const debtRatio = formatPct(safeGet(row, 'DEBT_RATIO', safeGet(row, '资产负债率')));
    report += `| ${periodStr} | ${totalAssets} | ${totalLiab} | ${totalEquity} | ${debtRatio} |\n`;
  }
  report += `\n`;
}

// 利润表
const income = financial.income_statement || [];
if (income.length > 0) {
  report += `## 二、利润表（最近${Math.min(income.length, 8)}期）\n\n`;
  report += `| 报告期 | 营业总收入 | 营业利润 | 净利润 |\n`;
  report += `|--------|-----------|---------|--------|\n`;
  for (const row of income.slice(0, 8)) {
    const period = safeGet(row, 'REPORT_DATE', safeGet(row, '报告期', '-'));
    const periodStr = period !== '-' ? String(period).substring(0, 10) : '-';
    const revenue = formatNum(safeGet(row, 'TOTAL_OPERATE_INCOME', safeGet(row, '营业总收入')));
    const operProfit = formatNum(safeGet(row, 'OPERATE_PROFIT', safeGet(row, '营业利润')));
    const netProfit = formatNum(safeGet(row, 'NETPROFIT', safeGet(row, '净利润')));
    report += `| ${periodStr} | ${revenue} | ${operProfit} | ${netProfit} |\n`;
  }
  report += `\n`;
}

// 现金流量表
const cashflow = financial.cash_flow || [];
if (cashflow.length > 0) {
  report += `## 三、现金流量表（最近${Math.min(cashflow.length, 8)}期）\n\n`;
  report += `| 报告期 | 经营活动现金流 | 投资活动现金流 | 筹资活动现金流 |\n`;
  report += `|--------|---------------|---------------|---------------|\n`;
  for (const row of cashflow.slice(0, 8)) {
    const period = safeGet(row, 'REPORT_DATE', safeGet(row, '报告期', '-'));
    const periodStr = period !== '-' ? String(period).substring(0, 10) : '-';
    const operCash = formatNum(safeGet(row, 'NETCASH_OPERATE', safeGet(row, '经营活动产生的现金流量净额')));
    const investCash = formatNum(safeGet(row, 'NETCASH_INVEST', safeGet(row, '投资活动产生的现金流量净额')));
    const financeCash = formatNum(safeGet(row, 'NETCASH_FINANCE', safeGet(row, '筹资活动产生的现金流量净额')));
    report += `| ${periodStr} | ${operCash} | ${investCash} | ${financeCash} |\n`;
  }
  report += `\n`;
}

// 财务指标（仅A股）
const indicator = financial.financial_indicator || [];
if (indicator.length > 0) {
  report += `## 四、主要财务指标（最近${Math.min(indicator.length, 8)}期）\n\n`;
  report += `| 报告期 | 每股收益 | 每股净资产 | 净资产收益率 | 毛利率 | 净利率 |\n`;
  report += `|--------|---------|-----------|-------------|--------|--------|\n`;
  for (const row of indicator.slice(0, 8)) {
    const period = safeGet(row, '报告期', '-');
    const periodStr = period !== '-' ? String(period).substring(0, 10) : '-';
    const eps = formatNum(safeGet(row, '摊薄每股收益', safeGet(row, '基本每股收益')));
    const bps = formatNum(safeGet(row, '每股净资产'));
    const roe = formatPct(safeGet(row, '净资产收益率'));
    const grossMargin = formatPct(safeGet(row, '销售毛利率'));
    const netMargin = formatPct(safeGet(row, '销售净利率'));
    report += `| ${periodStr} | ${eps} | ${bps} | ${roe} | ${grossMargin} | ${netMargin} |\n`;
  }
  report += `\n`;
}

// 数据质量说明
const hasBalance = balance.length > 0;
const hasIncome = income.length > 0;
const hasCashflow = cashflow.length > 0;
const hasIndicator = indicator.length > 0;

if (!hasBalance && !hasIncome && !hasCashflow && !hasIndicator) {
  report += `## 注意\n\n未能获取到该公司的财务数据，可能是数据源暂时不可用。\n`;
}

return {
  json: {
    userInputCompany: userInputCompany,  // 保持用户原始输入
    company: userInputCompany,           // 使用用户输入作为主要company字段
    stockCode: stockInfo.code,
    stockName: stockInfo.name,           // API返回的标准股票简称
    market: stockInfo.market,
    stockFound: true,
    hasFinancialData: hasBalance || hasIncome || hasCashflow || hasIndicator,
    financialReport: report,
    requestTime: inputData.requestTime
  }
};

parameters.text:
你是一位资深的商业分析师和投资顾问，精通波特的竞争战略理论和格雷厄姆的价值投资理论。

## 分析对象
**用户查询公司**：{{ $json.userInputCompany }}
**匹配股票代码**：{{ $json.stockCode }}（{{ $json.market }}股）
**标准股票简称**：{{ $json.stockName }}

## 财务数据

{{ $json.financialReport }}

## 分析要求

请基于上述财务数据进行深入分析。重要：你只需要分析财务数据，不要在 JSON 输出中填写股票基本信息（company、stock_info 等字段会由系统自动填充）。

**财务分析重点**：
1. **盈利能力**: 分析每股收益、净资产收益率、毛利率、净利率的变化趋势
2. **偿债能力**: 关注资产负债率、流动比率
3. **成长能力**: 分析营收增长率、净利润增长率，判断成长持续性
4. **现金流质量**: 评估经营活动现金流与净利润的匹配度

## 理论框架

### 波特五力分析
- 供应商议价能力、买方议价能力、潜在进入者威胁、替代品威胁、行业竞争程度

### 格雷厄姆价值投资标准
- 财务实力、盈利稳定性、盈利增长、合理估值

## 输出格式

请严格按以下 JSON 格式输出（不要包含 markdown 代码块标记）：

{
  "company": "PLACEHOLDER",
  "stock_info": {
    "code": "PLACEHOLDER",
    "name": "PLACEHOLDER",
    "market": "PLACEHOLDER"
  },
  "financial_analysis": {
    "data_quality": "数据质量评估（完整/部分/缺失）",
    "profitability": {
      "summary": "盈利能力分析（引用具体数字）"
    },
    "solvency": {
      "summary": "偿债能力分析"
    },
    "growth": {
      "summary": "成长能力分析"
    },
    "cash_flow": {
      "summary": "现金流质量分析"
    },
    "key_highlights": ["财务亮点1", "财务亮点2"],
    "key_concerns": ["财务风险点1", "财务风险点2"]
  },
  "competitive_analysis": {
    "industry_type": "行业类型",
    "five_forces": {
      "supplier_power": "供应商议价能力",
      "buyer_power": "买方议价能力",
      "new_entrants": "潜在进入者威胁",
      "substitutes": "替代品威胁",
      "rivalry": "行业竞争程度"
    },
    "competitive_advantage": "核心竞争优势",
    "moat": "护城河分析"
  },
  "investment_analysis": {
    "investor_type": "适合投资者类型（防御型/积极型）",
    "graham_criteria": {
      "financial_strength": {"met": true, "detail": "财务实力评估"},
      "earnings_stability": {"met": true, "detail": "盈利稳定性"},
      "earnings_growth": {"met": true, "detail": "盈利增长"},
      "valuation": {"met": true, "detail": "估值评估"}
    },
    "safety_margin": "安全边际评估",
    "risks": ["风险1", "风险2", "风险3"]
  },
  "conclusion": {
    "summary": "综合结论（300字以上，必须引用具体财务数据）",
    "rating": "推荐/中性/谨慎",
    "key_insights": ["洞察1", "洞察2", "洞察3"],
    "action_suggestion": "具体行动建议"
  }
}

**重要提示**：company 和 stock_info 字段请填写 "PLACEHOLDER"，这些字段会由系统自动替换为正确的值。你只需专注于分析财务数据即可。

parameters.jsCode:
// 处理 AI 输出，并强制使用正确的股票信息
let output = $input.item.json.output;

const inputData = $('生成财务报告').item.json;
const userInputCompany = inputData.userInputCompany;
const stockCode = inputData.stockCode;
const stockName = inputData.stockName;
const market = inputData.market;

if (!output) {
  throw new Error('AI 未返回有效输出');
}

if (typeof output === 'string') {
  output = output.replace(/^```json\s*/i, '').replace(/^```\s*/i, '').replace(/\s*```$/i, '').trim();

  try {
    output = JSON.parse(output);
  } catch (e) {
    return {
      json: {
        success: false,
        error: 'AI 输出格式错误',
        raw_output: output.substring(0, 1000)
      }
    };
  }
}

// 强制替换为正确的数据，防止 AI 幻觉或使用错误数据
if (output && typeof output === 'object') {
  output.company = userInputCompany;

  // 强制替换 stock_info，确保使用 API 返回的正确数据
  if (output.stock_info && typeof output.stock_info === 'object') {
    output.stock_info.code = stockCode;
    output.stock_info.name = stockName;
    output.stock_info.market = market;
  }
}

return {
  json: {
    success: true,
    data: output,
    metadata: {
      analyzed_at: new Date().toISOString(),
      version: '5.0.2-stock-info-fixed',
      data_source: 'AKShare',
      stock_found: inputData?.stockFound || false,
      has_financial_data: inputData?.hasFinancialData || false,
      user_input_company: userInputCompany
    }
  }
};
